import * as L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import "leaflet-routing-machine";
import "@asymmetrik/ngx-leaflet";
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/map.service";
import * as i2 from "src/app/services/shape.service";
const iconRetinaUrl = 'assets/marker-icon-2x.png';
const iconUrl = 'assets/marker-icon.png';
const shadowUrl = 'assets/marker-shadow.png';
const iconDefault = L.icon({
    iconRetinaUrl,
    iconUrl,
    shadowUrl,
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    tooltipAnchor: [16, -28],
    shadowSize: [41, 41]
});
L.Marker.prototype.options.icon = iconDefault;
export class MapComponent {
    constructor(markerService, shapeService) {
        this.markerService = markerService;
        this.shapeService = shapeService;
    }
    highlightFeature(e) {
        const layer = e.target;
        layer.setStyle({
            weight: 10,
            opacity: 1.0,
            color: '#DFA612',
            fillOpacity: 1.0,
            fillColor: '#FAE042'
        });
    }
    resetFeature(e) {
        const layer = e.target;
        layer.setStyle({
            weight: 3,
            opacity: 0.5,
            color: '#008f68',
            fillOpacity: 0.8,
            fillColor: '#6DB65B'
        });
    }
    initStatesLayer() {
        const stateLayer = L.geoJSON(this.states, {
            style: (feature) => ({
                weight: 3,
                opacity: 0.5,
                color: '#008f68',
                fillOpacity: 0.8,
                fillColor: '#6DB65B'
            }),
            onEachFeature: (feature, layer) => (layer.on({
                mouseover: (e) => (this.highlightFeature(e)),
                mouseout: (e) => (this.resetFeature(e)),
            }))
        });
        this.map.addLayer(stateLayer);
        stateLayer.bringToBack();
    }
    ngAfterViewInit() {
        this.initMap();
        this.markerService.makeBranchMarkers(this.map);
        // this.markerService.makeCapitalMarkers(this.map);
        // this.markerService.makeCapitalCircleMarkers(this.map);
        // this.shapeService.getStateShapes().subscribe(states => {
        //   this.states = states;
        //   this.initStatesLayer();
        // });
    }
    async initMap() {
        this.map = L.map('map', {
            center: [4.687645, 107.969057],
            zoom: 7
        });
        var control = L.Routing.control({
            waypoints: [L.latLng(1.643081325, 103.611114084), L.latLng(1.665021904, 103.59419664)],
            addWaypoints: false,
            routeWhileDragging: false,
            fitSelectedRoutes: false
        }).addTo(this.map);
        // await new Promise(() => setTimeout(() =>{
        //   control.hide();
        // }, 1000));
        control.hide();
        // this.map.removeControl(control);
        control.on('routesfound', function (e) {
            var routes = e.routes;
            var summary = routes[0].summary;
            var distance = summary.totalDistance / 1000;
            var time = summary.totalTime / 60;
            var minute = Math.trunc(time);
            var second = (time - minute) * 60;
            // alert('Total distance is ' + summary.totalDistance / 1000 + ' km and total time is ' + Math.round(summary.totalTime % 3600 / 60) + ' minutes');
            console.log('Total distance is ' + Math.round((distance + Number.EPSILON) * 100) / 100 + ' km and total time is ' + minute + ' minutes ' + Math.round(second) + ' seconds');
        });
        // L.Routing.control({
        //   waypoints: [L.latLng(1.643081325, 103.611114084), L.latLng(1.665021904, 103.59419664)],
        //   addWaypoints: false,
        //   routeWhileDragging: false,
        //   fitSelectedRoutes: false
        // }).addTo(this.map);
        const tiles = L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            maxZoom: 18,
            minZoom: 3,
            attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        });
        tiles.addTo(this.map);
    }
}
